/* import React from "react";
import logo from "./logo.svg";
import "./App.css"; */
import React, { useState } from "react";

import Jumbotron from "react-bootstrap/Jumbotron";
import Toast from "react-bootstrap/Toast";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import Navbar from "react-bootstrap/Navbar";
import { Form, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import Image from "react-bootstrap/Image";

import "./App.css";

const ExampleToast = ({ children }) => {
  const [show, toggleShow] = useState(true);

  return (
    <>
      {!show && <Button onClick={() => toggleShow(true)}>Beep Boop</Button>}
      <Toast show={show} onClose={() => toggleShow(false)}>
        <Toast.Header>
          <strong className="mr-auto">yummy</strong>
        </Toast.Header>
        <Toast.Body>{children}</Toast.Body>
      </Toast>
    </>
  );
};

const ChocChip = () => {
  return (
    <div>
      <h2>chocolate chip</h2>
      <img src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.bettycrocker.com%2Frecipes%2Fultimate-chocolate-chip-cookies%2F77c14e03-d8b0-4844-846d-f19304f61c57&psig=AOvVaw3wQ2ZAEjGfnTrwd02SwKtc&ust=1588824332646000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCMCjpoKunukCFQAAAAAdAAAAABAD">
        {" "}
      </img>
    </div>
  );
};

const App = () => (
  <Container fluid>
    <Jumbotron>
      <ChocChip />
      <h1 className="header">Welcome To The Cookie Site</h1>
      <ExampleToast>
        Check out some epic treats
        <span role="img" aria-label="tada">
          üç™
        </span>
      </ExampleToast>
    </Jumbotron>
  </Container>
);

export default App;
