/* import React from "react";
import logo from "./logo.svg";
import "./App.css"; */
import React, { useState } from "react";

import Jumbotron from "react-bootstrap/Jumbotron";
import Toast from "react-bootstrap/Toast";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";

import "./App.css";

const ExampleToast = ({ children }) => {
  const [show, toggleShow] = useState(true);

  return (
    <>
      {!show && <Button onClick={() => toggleShow(true)}>Beep Boop</Button>}
      <Toast show={show} onClose={() => toggleShow(false)}>
        <Toast.Header>
          <strong className="mr-auto">yummy</strong>
        </Toast.Header>
        <Toast.Body>{children}</Toast.Body>
      </Toast>
    </>
  );
};

const App = () => (
  <Container className="p-3">
    <Jumbotron>
      <h1 className="header">Welcome To The Cookie Site</h1>
      <ExampleToast>
        Check out some epic treats
        <span role="img" aria-label="tada">
          üç™
        </span>
      </ExampleToast>
    </Jumbotron>
    var Navbar = ReactBootstrap.Navbar, Nav = ReactBootstrap.Nav, NavItem =
    ReactBootstrap.NavItem, DropdownButton = ReactBootstrap.DropdownButton,
    MenuItem = ReactBootstrap.MenuItem; var navbarInstance = (
    <Navbar>
      <Nav>
        <NavItem eventKey={1} href="#">
          Link
        </NavItem>
        <NavItem eventKey={2} href="#">
          Link
        </NavItem>
        <DropdownButton eventKey={3} title="Dropdown">
          <MenuItem eventKey="1">Action</MenuItem>
          <MenuItem eventKey="2">Another action</MenuItem>
          <MenuItem eventKey="3">Something else here</MenuItem>
          <MenuItem divider />
          <MenuItem eventKey="4">Separated link</MenuItem>
        </DropdownButton>
      </Nav>
    </Navbar>
    ); React.render(navbarInstance, document.getElementById('navbarNode'));
  </Container>
);

export default App;
